import React, { Component } from 'react';
import './Userlist.css';
import * as firebase from 'firebase';
import AddUserForm from './AddUserForm';

let newState = [];
let secondxyz;
let newuserid;
let word;
// var base64Img = require('base64-img');

class UserList extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            secondxyz : [] ,
            newState: [],
            redirect: false,
        }

        this.handleDelete = this.handleDelete.bind(this);
    }

    handleDelete = (e) => {
        e.preventDefault();
        // alert("We r On Delete");
        console.log("We r Here on delete");

        var ref = firebase.database().ref().child('/' )
        ref.once("value")
        .then(function(snapshot) {
           snapshot.ref.remove();
           alert("user deleted..!");
       }).catch(function(error) {alert("Data could not be deleted." + error);});
    }

    renderTableData() {

        return this.state.secondxyz.map((word,index) => {
            const { email, mobileno, name, password} = word 
            return (
                <tr >
                <td>{email}</td>
                <td>{mobileno}</td>
                <td>{name}</td>
                <td>{password}</td>
                <td><button>Edit</button></td>
                <td><button onClick={this.handleDelete}>Delete</button></td>
                </tr>
            )
        });
     }

    renderTableHeader() {
        let header = Object.keys(this.state.secondxyz[0])
        return header.map((key, index) => {
           return <th key={index}>{key.toUpperCase()}</th>
        })
    }

    componentDidMount () {

        const xyz = firebase.database().ref('/');
        xyz.on('value',(snapshot) => {
            secondxyz = snapshot.val();
            newuserid = snapshot.key();
            console.log("secondxyz :: ", secondxyz);
            console.log("newuser_id :: ", newuserid);


            for (word in secondxyz){
                newState.push({
                    email : secondxyz[word].email,
                    mobileno : secondxyz[word].mobileno,
                    name : secondxyz[word].name,
                    password : secondxyz[word].password
                });
            }
            this.setState({
                secondxyz: newState
            });  
        })

    }

    handleAdduser = () => {

        this.setState({
            redirect: true
        })

    }

    render () {

    const { redirect } = this.state;
    if (redirect) {

        // return <Lntframe />;
        return <AddUserForm />;

    }

    return (

    <div>
        {/* <div>
        <button className="button" onClick={this.handleAdduser}>Add User</button>
        </div> */}
        <table id='students'>
            <tbody>
                <tr>
                    <th>EMAIL</th>
                    <th>Mobile NO</th>
                    <th>NAME</th>
                    <th>PASSWORD</th>
                    <th>EDIT</th>
                    <th>DELETE</th>
                    {/* <th>DATE</th> */}
                </tr>

                {this.renderTableData()}
            </tbody>
        </table>
        </div>

    );
    }
    }

export default UserList;
