import './index.css';
import './Register.css';

class AddUserForm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            name : '',
            email: '',
            password: '',
            mobileno: '',
            redirect: false,
        }

        this.onSubmit = this.onSubmit.bind(this);
    }

    handleInputChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    onSubmit = (e) => {
        e.preventDefault();

        // const { email, password } = this.state;
        // firebase
        //  .auth()
        //  .signInWithEmailAndPassword(email, password)
        //  .then((user) => {
        //     // window.location.href = "/Lntframe";
        //     // alert("yes welcome")
        //        this.setState({
        //             redirect: true
        //         })
        // })
        //  .catch((error) => {
        //     var errorCode = error.code;
        //     var errorMessage = error.message;
        //     console.log("CATCH :: " , errorCode);
        //     console.log("CATCH SECOND :: " , errorMessage);

        // });

        username = document.getElementById('user-name').value;
        userpassword = document.getElementById('user-password').value;
        var complainData={
            username:username,
            password:userpassword 
        }
        database = firebase.database();
        dbmydata = database.ref("/");
        console.log("MY DB Data :: " , dbmydata);
        dbmydata.push(complainData).then((val) => {
            console.log("val",val);
            console.log("Sccess data");
        });

        // alert(username + " Comeon  " + userpassword);

    }

  render () {

    return (

    <div>
        <div>
            <h2 className="App">Add User</h2>

            <form 
                onSubmit={this.onSubmit} 
                className="projects fbca fbca--dircol fbca--flst">
                <div className="inputrow fbca">
                    <label className="inputrow__description" htmlFor="username">Name</label>
                    <input
                        id="name"
                        // value={name}  
                        // onChange={this.handleInputChange}
                        type="text" 
                        className="inputrow__field" 
                        name="name" />
                </div>
                <div className="inputrow fbca">
                    <label className="inputrow__description" htmlFor="username">Email</label>
                    <input
                        id="email"
                        // value={email} 
                        // onChange={this.handleInputChange}
                        type="text" 
                        className="inputrow__field" 
                        name="email" />
                </div>

                <div className="inputrow fbca">
                    <label className="inputrow__description" htmlFor="password">Password</label>
                    <input
                        id="password"
                        // value={password}
                        // onChange={this.handleInputChange} 
                        type="password" 
                        className="inputrow__field" 
                        name="password" />
                </div>
                <div className="inputrow fbca">
                    <label className="inputrow__description" htmlFor="password">Mobile No</label>
                    <input
                        id="mobileno"
                        // value={mobileno} 
                        // onChange={this.handleInputChange}
                        type="number" 
                        className="inputrow__field" 
                        name="mobileno" />
                </div>
                <div className="btn-group-submit">
                    <button 
                    className="button"> Add </button>
                    {/* <Link  className="btn btn-link">Register</Link> */}
                </div>


            </form>
        </div>
    </div>

    );
  }
  }

  export default AddUserForm;
