        //     redirect: false,
        // }

        this.onSubmit = this.onSubmit.bind(this);
    }

    // handleInputChange = (event) => {
    //     this.setState({ [event.target.name]: event.target.value });
    // };

    onSubmit = (e) => {
        e.preventDefault();

        // const { email, password } = this.state;
        // firebase
        //  .auth()
        //  .signInWithEmailAndPassword(email, password)
        //  .then((user) => {
